mapper.select:
select id, user_name, age
from users
where id = #{id};

select id, user_name, age
from users
where user_name = #{name};

select id, user_name, age
from users
where user_name = #{userName}
  and password = #{password};

select id, user_name, age
from users
where id in (select distinct vid from messages where pid = #{id});
